function sendAction(a){if(window.HttpAPI){window.HttpAPI.sendPoint("action&action="+a)}if(gConst.logModel){util.printGreen("sendAction id = "+a)}}function removeLoading(){var a=document.getElementById("loader-placeholder");if(a){a.className="f-dn"}}function gameStart(){removeLoading();if(playEnterSound){playEnterSound("resource/assets/sound/"+gConst.bgmName+".mp3",arguments)}window.app.showGame();if(gConst.logModel){util.printGreen("gameStart")}}(function(){if(!gConst.packModel){window.onload=function(){gameStart()}}})();function gameClose(){if(destorySound){destorySound()}if(gConst.logModel){util.printGreen("gameClose")}}function ready(){if(window.gameReady){window.gameReady()}if(gConst.logModel){util.printGreen("gameReady")}}function gameInstall(){if(window.install){window.install()}if(gConst.logModel){util.printGreen("install")}}function end(){if(window.gameEnd){window.gameEnd()}if(gConst.logModel){util.printGreen("gameEnd")}}function retry(){if(window.gameRetry){window.gameRetry()}if(gConst.logModel){util.printGreen("gameRetry")}};